-- 入库记录表
CREATE TABLE `inbound` (
   `id` INT AUTO_INCREMENT  COMMENT '主键ID',
   `product_id` varchar(18) COMMENT '产品id, 在本业务情况下，存储的就是材料规格表的hh字段',
   `quantity` INT NOT NULL  COMMENT '数量',
   `inbound_date` INT NOT NULL  COMMENT '入库时间，时间戳(秒)',
   `created_at` INT  COMMENT '创建时间 时间戳(秒)',
   PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;


-- 出库记录表
CREATE TABLE `outbound` (
    `id` INT AUTO_INCREMENT COMMENT '主键ID',
    `product_id` varchar(18) COMMENT '产品id, 在本业务情况下，存储的就是材料规格表的hh字段',
    `quantity` INT NOT NULL COMMENT '数量',
    `outbound_date` INT NOT NULL COMMENT '出库时间，时间戳(秒)',
    `created_at` INT COMMENT '创建时间 时间戳(秒)',
    PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;



-- 入库出库日志表(为了学习触发器)
CREATE TABLE `in_outbound_logs` (
    `id` INT AUTO_INCREMENT COMMENT '主键ID',
    `ops` int COMMENT '操作类型 0-入库  1-出库',
    `record_id` INT NOT NULL COMMENT '出库入库记录id',
    PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;




